					<div class="invoice_info"><?php printf( '<span class="info">%1$s</span> %2$s', __( 'Issued on', 'sprout-invoices' ), date_i18n( get_option( 'date_format' ), si_get_invoice_issue_date() ) ) ?></div>

					<div class="invoice_info"><?php printf( '<span class="info">%1$s</span> %2$s', __( 'Due on', 'sprout-invoices' ), date_i18n( get_option( 'date_format' ), si_get_invoice_due_date() ) ) ?></div>
					
					<?php if ( si_has_invoice_deposit( get_the_id(), true ) ) : ?>
						<div id="deposit_total" class="invoice_info"><?php printf( '<span class="info">%1$s</span> %2$s', __( 'Deposit Due', 'sprout-invoices' ), sa_get_formatted_money( si_get_invoice_deposit() ) ) ?></div>
					<?php endif ?>

					<div id="total_due" class="invoice_info"><?php printf( '<span class="info">%1$s</span> <span class="total">%2$s</span>', __( 'Balance Due', 'sprout-invoices' ), sa_get_formatted_money( si_get_invoice_balance() ) ) ?></div>

				</div>

			</div>
		</section>

		<section class="row" id="details">
			<div class="inner">
				<div class="row item">
